package com.rezza.xsisapp.viewimport android.content.Contextimport android.util.AttributeSetimport android.view.LayoutInflaterimport android.widget.RelativeLayoutabstract class MyView(context: Context?, attrs: AttributeSet?) : RelativeLayout(context, attrs) {    init {        LayoutInflater.from(context).inflate(this.setlayout(), this, true)        this.initLayout()        this.initListener()        this.initData()    }    protected abstract fun setlayout(): Int    protected abstract fun initLayout()    protected abstract fun initListener()    protected fun initData() {}}